import React, { Component } from 'react';
import styled from 'styled-components';
import './App.css';

const Wrapper = styled.section`
  background: #20beec;
`;

const Button = styled.button`
  font-size: 1.3rem;
  color: #ffffff;
  border: none;
  border-radius: 5px;
  padding: 7px 10px;
  background: #e82010;
  &:hover {
    background: #ff7000;
  }
`;

const H1 = styled.h1`
  color: #ffffff;
  font-size: 4em;
  border-radius: 5px;
  margin: 10px;
  font-family: 'Arial';
`;

const Input = styled.input`
  width: 20%;
  padding: 10px 15px;
  margin: 4 px 0;
  border-radius: 4px;
`;

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      first: '',
      second: ''
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  async handleSubmit(e) {
    e.preventDefault();
  }
  handleChange(e) {
    let target = e.target;
    let value = target.type === 'checkbox' ? target.checked : target.value;
    let name = target.name;

    this.setState({
      [name]: value
    });
  }

  calculate() {
    let x = this.state.first;
    let y = this.state.second;
    let oper = document.getElementsByName('operator').value;
    if (oper === '+') {
      document.getElementById('result').value = x + y;
      console.log('Added');
    }
    if (oper === '-') {
      document.getElementById('result').value = x - y;
      console.log('Added');
    }
    if (oper === 'x') {
      document.getElementById('result').value = x * y;
      console.log('Added');
    }
    if (oper === '/') {
      document.getElementById('result').value = x / y;
      console.log('Added');
    }
  }

  render() {
    return (
      <Wrapper>
        <div className="App">
          <H1> Calculator</H1>
          <form onChange={this.handleChange}>
            <div className="input-group">
              <Input
                type="number"
                name="first"
                placeholder="Enter the first number"
                value={this.state.first}
                onChange={this.handleChange}
              />
            </div>
            <div className="input-group">
              <Input
                type="number"
                name="second"
                placeholder="Enter the second number"
                value={this.state.second}
                onChange={this.handleChange}
              />
            </div>
            <div className="input-group">
              <select name="operator" onChange={this.handleChange}>
                <option value="+">+</option>
                <option value="-">-</option>
                <option value="x">x</option>
                <option value="/">/</option>
              </select>
              <Button onClick={this.calculate}>Count</Button>
            </div>
            <div>
              <input type="text" id="result" placeholder="0" />
            </div>
          </form>
        </div>
      </Wrapper>
    );
  }
}

export default App;
