import React, { Component } from 'react';
import styled, { ThemeProvider } from 'styled-components';
import './App.css';
import Display from './Display';

const theme = {
  primary: 'white',
  secondary: 'cyan',
  font: 'sans-serif'
};

const Button = styled.button`
  font-family: ${props => props.theme.font};
  font-size: 1.3rem;
  border: none;
  border-radius: 5px;
  padding: 7px 10px;
  background: 'red';
  &:hover {
    background: 'blue';
  }
`;

const H1 = styled.h1`
  font-size: 3rem;
`;

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      first: '',
      second: '',
      operator: '',
      result: '',
      display: '0'
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  async handleSubmit(e) {
    e.preventDefault();
  }
  handleChange(e) {
    let target = e.target;
    let value = target.type === 'checkbox' ? target.checked : target.value;
    let name = target.name;

    this.setState({
      [name]: value
    });
  }

  calculate() {
    if (this.state.equation.includes('=')) {
      let val = `${this.state.display} = ${this.state.display}`;
      this.setState({
        equation: val
      });
    } else if (
      this.state.equation != '' &&
      this.state.equation.match(/[+\-*\/]/) != null &&
      this.state.equation.match(/[+\-*\/]$/) == null
    ) {
      let result = Number.isInteger(eval(this.state.equation))
        ? eval(this.state.equation)
        : parseFloat(eval(this.state.equation).toFixed(5));
      let val = this.state.equation;
      val += ` = ${result}`;
      this.setState({
        display: result,
        equation: val
      });
    }
  }

  render() {
    return (
      <ThemeProvider theme={theme}>
        <div className="App">
          <H1> Calculator</H1>
          <form onSubmit={this.handleSubmit}>
            <div className="input-group">
              <input
                type="number"
                name="first"
                placeholder="Enter the first number"
                value={this.state.first}
                onChange={this.handleChange}
              />
            </div>
            <div className="input-group">
              <input
                type="number"
                name="second"
                placeholder="Enter the second number"
                value={this.state.second}
                onChange={this.handleChange}
              />
            </div>
            <div className="input-group">
              <select>
                <option>+</option>
                <option>-</option>
                <option>*</option>
                <option>/</option>
              </select>
              <Button primary onSubmit={this.calculate}>
                Count
              </Button>
            </div>
            <div>
              <Display data={this.state.display} />
            </div>
          </form>
        </div>
      </ThemeProvider>
    );
  }
}

export default App;
