import React, { Component } from 'react';
import styled, { ThemeProvider } from 'styled-components';
import './App.css';

const theme = {
  primary: 'white',
  secondary: 'cyan',
  font: 'sans-serif'
};

const Wrapper = styled.section`
  padding: 7px 10px;
  background: 'teal';
`;

const Button = styled.button`
  font-family: ${props => props.theme.font};
  font-size: 1.3rem;
  border: none;
  border-radius: 5px;
  padding: 7px 10px;
  background: 'red';
  &:hover {
    background: 'blue';
  }
`;

const H1 = styled.h1`
  font-size: 3rem;
`;

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      first: '',
      second: ''
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  async handleSubmit(e) {
    e.preventDefault();
  }
  handleChange(e) {
    let target = e.target;
    let value = target.type === 'checkbox' ? target.checked : target.value;
    let name = target.name;

    this.setState({
      [name]: value
    });
  }

  calculate() {
    let x = this.state.first;
    let y = this.state.second;
    let oper = document.getElementsByName('operator').value;
    if (oper === '+') {
      document.getElementById('result').value = x + y;
      console.log('Added');
    }
    if (oper === '-') {
      document.getElementById('result').value = x - y;
      console.log('Added');
    }
    if (oper === 'x') {
      document.getElementById('result').value = x * y;
      console.log('Added');
    }
    if (oper === '/') {
      document.getElementById('result').value = x / y;
      console.log('Added');
    }
  }

  render() {
    return (
      <ThemeProvider theme={theme}>
        <Wrapper>
          <div className="App">
            <H1> Calculator</H1>
            <form onChange={this.handleSubmit}>
              <div className="input-group">
                <input
                  type="number"
                  name="first"
                  placeholder="Enter the first number"
                  value={this.state.first}
                  onChange={this.handleChange}
                />
              </div>
              <div className="input-group">
                <input
                  type="number"
                  name="second"
                  placeholder="Enter the second number"
                  value={this.state.second}
                  onChange={this.handleChange}
                />
              </div>
              <div className="input-group">
                <select name="operator" onChange={this.handleChange}>
                  <option value="+">+</option>
                  <option value="-">-</option>
                  <option value="x">x</option>
                  <option value="/">/</option>
                </select>
                <Button primary onClick={this.calculate}>
                  Count
                </Button>
              </div>
              <div>
                <input type="text" id="result" placeholder="0" />
              </div>
            </form>
          </div>
        </Wrapper>
      </ThemeProvider>
    );
  }
}

export default App;
